import { useState, useEffect } from "react";
import VideoList from "../components/VideoList";
import Video from "../components/Video";

const VideoContainer = ({ videos, fetchTopic }) => {
  const [selectedVideo, setSelectedVideo] = useState({});

  useEffect(() => {
    fetchTopic();
  }, [selectedVideo, fetchTopic]);

  const handleChange = (event) => {
    const selectedVideo = videos.find((video) => {
      if (video.title === event.target.value) {
        return video;
      }
      setSelectedVideo(selectedVideo);
    });
  };

  return (
    <>
      {videos && <Video selectedVideo={selectedVideo} />}
      <VideoList videos={videos} handleChange={handleChange} />
    </>
  );
};

export default VideoContainer;
