import React from "react";
import LinkList from "./LinkList";

const EducationalDescription = ({ topic }) => {

  const linkItems = topic.map((link) => {
    return (
      <LinkList link={link} />
    )

  })

    return (
        <>
          <h2>{topic.title}</h2>
          <h3>{topic.name}</h3>
          <p>{topic.description}</p>
          <ul>{linkItems}</ul>
        </>
    )
}

export default EducationalDescription;
